	cursor: pointer;
	border-radius: 10px;
}
.photo {
	position: relative;
	flex: 0 0 240px;
	width: 240px;
	height: 320px;
	background-color: #333;
	overflow: hidden;
	border: none;
	border-radius: 10px;
	box-shadow:
		rgba(0, 0, 0, 0.66) 0 30px 60px 0,
