}

canvas {
	cursor: crosshair;
	display: block;
	position: relative;
	z-index: 3;
}

canvas:active {
	cursor: crosshair;
}

#skyline {
	background: url(https://frontend-studio-1256354221.file.myqcloud.com/img/skyline.png) repeat-x 50% 0;
	bottom: 0;
	height: 135px;
	left: 0;
	position: absolute;
	width: 100%;
	z-index: 1;
}

#mountains1 {
	background: url(https://frontend-studio-1256354221.file.myqcloud.com/img/mountains1.png) repeat-x 40% 0;
	bottom: 0;
	height: 200px;
	left: 0;
	position: absolute;
	width: 100%;
	z-index: 1;
}

#mountains2 {
	background: url(https://frontend-studio-1256354221.file.myqcloud.com/img/mountains2.png) repeat-x 30% 0;
	bottom: 0;
	height: 250px;
	left: 0;
	position: absolute;
	width: 100%;
	z-index: 1;
}

.pre {
	left: 0;
	margin: 0;
	position: absolute;
	padding: 16px;
	top: 0;
	z-index: 1;
}

.pre p {
	font-size: 14px;
	margin: 0;
	padding: 0;
	line-height: 1.5;
	tab-size: 4;
	color: #dcdcdc; 
}

.info {
	position: absolute;
	left: 0;
	bottom: 0;
	font-size: 14px;
	color: #ddd;
	z-index: 1;
	animation: twinkling 2s infinite ease-in-out;
}

@keyframes twinkling {
	0% {
		opacity: 0;
	}

	100% {
		opacity: 1;
	}
}

